import React, { useEffect, useState } from 'react';
import { Button, Card, CardBody, Input, Select, SelectItem } from '@heroui/react';
import { Icon } from '@iconify/react';
import BaseInteractiveLayout from './BaseInteractiveLayout';
import { InteractiveComponentProps } from './types';

export interface MatrixColumn {
  key: string;
  labelZh: string;
  labelEn: string;
  type?: 'text' | 'number' | 'select';
  optionsZh?: string[];
  optionsEn?: string[];
}

interface MatrixRow {
  id: string;
  values: Record<string, any>;
}

const defaultColumns: MatrixColumn[] = [
  { key: 'name', labelZh: '名称', labelEn: 'Name', type: 'text' },
  { key: 'importance', labelZh: '重要度(1-5)', labelEn: 'Importance(1-5)', type: 'number' },
  { key: 'satisfaction', labelZh: '满意度(1-5)', labelEn: 'Satisfaction(1-5)', type: 'number' },
  { key: 'notes', labelZh: '备注', labelEn: 'Notes', type: 'text' },
];

const GenericMatrixDiagram: React.FC<InteractiveComponentProps & {
  title?: { zh: string; en: string };
  initialColumns?: MatrixColumn[];
}> = ({ lang = 'zh', onExport, showExportButtons = true, className = '', initialColumns, title, data }) => {
  const [columns] = useState<MatrixColumn[]>(initialColumns && initialColumns.length ? initialColumns : defaultColumns);
  const [rows, setRows] = useState<MatrixRow[]>(() => {
    if (data && Array.isArray(data)) {
      return (data as any[]).map((v, idx) => ({ id: `r${idx + 1}`, values: v }));
    }
    return [
      { id: 'r1', values: { name: lang === 'zh' ? '示例项' : 'Sample', importance: 4, satisfaction: 2, notes: '' } },
    ];
  });
  const [isEditing, setIsEditing] = useState<boolean>(false);

  useEffect(() => {
    // noop for now
  }, []);

  const addRow = () => {
    setRows((prev) => [
      ...prev,
      { id: `r${Date.now()}`, values: { name: '', importance: 3, satisfaction: 3, notes: '' } },
    ]);
  };

  const updateCell = (rowId: string, colKey: string, value: any) => {
    setRows((prev) => prev.map((r) => (r.id === rowId ? { ...r, values: { ...r.values, [colKey]: value } } : r)));
  };

  const deleteRow = (rowId: string) => {
    setRows((prev) => prev.filter((r) => r.id !== rowId));
  };

  const handleExport = (type: 'png' | 'csv') => {
    if (type === 'csv') {
      const csvData = rows.map((r) => {
        const obj: Record<string, any> = {};
        columns.forEach((c) => {
          const header = lang === 'en' ? c.labelEn : c.labelZh;
          const v = r.values[c.key];
          if (v && typeof v === 'object' && ('zh' in v || 'en' in v)) {
            obj[header] = v[lang] ?? v.zh ?? v.en ?? '';
          } else {
            obj[header] = v ?? '';
          }
        });
        return obj;
      });
      onExport?.('csv', csvData);
    } else {
      onExport?.('png', { columns, rows });
    }
  };

  const controls = (
    <div className="flex items-center gap-2">
      <Button size="sm" variant="flat" color="primary" startContent={<Icon icon="lucide:plus" />} onPress={addRow}>
        {lang === 'zh' ? '添加行' : 'Add Row'}
      </Button>
      <Button
        size="sm"
        variant={isEditing ? 'solid' : 'flat'}
        color={isEditing ? 'primary' : 'default'}
        startContent={<Icon icon={isEditing ? 'lucide:check' : 'lucide:edit-3'} />}
        onPress={() => setIsEditing(!isEditing)}
      >
        {isEditing ? (lang === 'zh' ? '完成' : 'Done') : (lang === 'zh' ? '编辑' : 'Edit')}
      </Button>
    </div>
  );

  const getDisplay = (val: any): string => {
    if (val && typeof val === 'object' && ('zh' in val || 'en' in val)) {
      return val[lang] ?? val.zh ?? val.en ?? '';
    }
    return String(val ?? '');
  };

  const getSelectedKey = (col: MatrixColumn, val: any): string => {
    const arr = lang === 'en' ? (col.optionsEn || []) : (col.optionsZh || []);
    const display = getDisplay(val);
    const idx = arr.findIndex((x) => x === display);
    return idx >= 0 ? String(idx) : '';
  };

  return (
    <BaseInteractiveLayout
      title={title ? (lang === 'en' ? title.en : title.zh) : (lang === 'zh' ? '矩阵表（可编辑）' : 'Editable Matrix')}
      titleEn={title ? title.en : 'Editable Matrix'}
      lang={lang}
      onExport={handleExport}
      showExportButtons={showExportButtons}
      controls={controls}
      className={className}
      data={{ columns, rows }}
      pathLabels={{ chapter: lang === 'zh' ? '第2章' : 'Chapter 2' }}
    >
      <Card>
        <CardBody className="p-0 overflow-x-auto">
          <table className="w-full text-sm">
            <thead>
              <tr className="bg-default-50">
                {columns.map((c) => (
                  <th key={c.key} className="px-4 py-3 text-left text-default-600">
                    {lang === 'en' ? c.labelEn : c.labelZh}
                  </th>
                ))}
                <th className="px-4 py-3" />
              </tr>
            </thead>
            <tbody>
              {rows.map((r) => (
                <tr key={r.id} className="border-t">
                  {columns.map((c) => (
                    <td key={c.key} className="px-4 py-2 align-middle">
                      {isEditing ? (
                        c.type === 'select' ? (
                          <Select
                            aria-label="select"
                            selectedKeys={[getSelectedKey(c, r.values[c.key])]}
                            onSelectionChange={(keys) => {
                              const idx = parseInt(Array.from(keys)[0] as string, 10);
                              const zhVal = (c.optionsZh || [])[idx] ?? '';
                              const enVal = (c.optionsEn || [])[idx] ?? zhVal;
                              updateCell(r.id, c.key, { zh: zhVal, en: enVal });
                            }}
                            size="sm"
                          >
                            {(lang === 'en' ? (c.optionsEn || []) : (c.optionsZh || [])).map((opt, i) => (
                              <SelectItem key={String(i)}>{opt}</SelectItem>
                            ))}
                          </Select>
                        ) : (
                          <Input
                            size="sm"
                            type={c.type === 'number' ? 'number' : 'text'}
                            value={c.type === 'number' ? String(r.values[c.key] ?? '') : getDisplay(r.values[c.key])}
                            onChange={(e) => {
                              if (c.type === 'number') {
                                updateCell(r.id, c.key, Number(e.target.value));
                              } else {
                                const existing = r.values[c.key] && typeof r.values[c.key] === 'object' ? r.values[c.key] : { zh: '', en: '' };
                                updateCell(r.id, c.key, { ...existing, [lang]: e.target.value });
                              }
                            }}
                          />
                        )
                      ) : (
                        <span className="text-default-700">{getDisplay(r.values[c.key])}</span>
                      )}
                    </td>
                  ))}
                  <td className="px-4 py-2 text-right">
                    {isEditing && (
                      <Button size="sm" variant="flat" color="danger" isIconOnly onPress={() => deleteRow(r.id)}>
                        <Icon icon="lucide:trash" />
                      </Button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </CardBody>
      </Card>
    </BaseInteractiveLayout>
  );
};

export default GenericMatrixDiagram;

src/components/interactive/types.ts.content:
// Interactive Component Types
// Base interface for all interactive components

export interface BaseInteractiveProps {
  lang: 'zh' | 'en';
  data?: any;
  onDataChange?: (data: any) => void;
  onExport?: (type: 'png' | 'csv', data: any) => void;
}

export interface ExportOptions {
  png: boolean;
  csv: boolean;
}

// North Star Framework Types
export interface NorthStarNode {
  id: string;
  parentId: string | null;
  name: string;
  nameEn?: string;
  type: 'nsm' | 'driver' | 'input';
  unit?: string;
  owner?: string;
  value?: number;
  target?: number;
  description?: string;
  descriptionEn?: string;
}

export interface NorthStarHypothesis {
  id: string;
  text: string;
  textEn?: string;
  priority: 'high' | 'medium' | 'low';
  validated: boolean;
  confidence?: number;
}

export interface NorthStarData {
  nodes: NorthStarNode[];
  hypotheses: NorthStarHypothesis[];
}

// Porter's Five Forces Types
export interface ForceData {
  key: string;
  labelZh: string;
  labelEn: string;
  weight: number; // 1-5
  description?: string;
  descriptionEn?: string;
  factors?: string[];
  factorsEn?: string[];
}

export interface PorterData {
  forces: ForceData[];
  overallRating?: number;
  summary?: string;
  summaryEn?: string;
}

// RACI Chart Types
export interface RaciRole {
  id: string;
  name: string;
  nameEn?: string;
  department?: string;
  avatar?: string;
}

export interface RaciTask {
  id: string;
  name: string;
  nameEn?: string;
  category?: string;
  description?: string;
  descriptionEn?: string;
}

export interface RaciAssignment {
  taskId: string;
  roleId: string;
  responsibility: 'R' | 'A' | 'C' | 'I';
}

export interface RaciData {
  roles: RaciRole[];
  tasks: RaciTask[];
  assignments: RaciAssignment[];
}

// Five Dysfunctions Types
export interface Dysfunction {
  key: string;
  labelZh: string;
  labelEn: string;
  level: number; // 1-5 (bottom to top)
  symptoms: string[];
  symptomsEn?: string[];
  solutions: string[];
  solutionsEn?: string[];
  score: number; // 1-5 assessment score
}

export interface TeamDysfunctionData {
  dysfunctions: Dysfunction[];
  teamName?: string;
  assessmentDate?: string;
  overallScore?: number;
}

// PESTLE Analysis Types
export interface PestleFactor {
  id: string;
  dimension: 'Political' | 'Economic' | 'Social' | 'Technological' | 'Legal' | 'Environmental';
  factor: string;
  factorEn?: string;
  impact: number; // 1-5
  direction: 'opportunity' | 'threat' | 'neutral';
  timeframe: 'short' | 'medium' | 'long';
  description?: string;
  descriptionEn?: string;
}

export interface PestleData {
  factors: PestleFactor[];
  summary?: string;
  summaryEn?: string;
}

// sSWOT Analysis Types
export interface SwotFactor {
  id: string;
  type: 'strength' | 'weakness' | 'opportunity' | 'threat';
  content: string;
  contentEn?: string;
  priority: number; // 1-5
}

export interface SwotStrategy {
  id: string;
  type: 'SO' | 'ST' | 'WO' | 'WT';
  title: string;
  titleEn?: string;
  description: string;
  descriptionEn?: string;
  priority: number; // 1-5
  feasibility: number; // 1-5
  impact: number; // 1-5
}

export interface SSwotData {
  situation: string;
  situationEn?: string;
  factors: SwotFactor[];
  strategies: SwotStrategy[];
}

// Business Opportunity Statement Types
export interface OpportunityData {
  who: string;
  whoEn?: string;
  what: string;
  whatEn?: string;
  why: string;
  whyEn?: string;
  how: string;
  howEn?: string;
  assumptions: string[];
  assumptionsEn?: string[];
  validationPlan: string[];
  validationPlanEn?: string[];
}

// Gantt Chart Types
export interface GanttTask {
  id: string;
  name: string;
  nameEn?: string;
  startDate: string;
  endDate: string;
  progress: number; // 0-100
  owner?: string;
  dependencies: string[]; // task IDs
  type: 'task' | 'milestone';
  category?: string;
  description?: string;
  descriptionEn?: string;
}

export interface GanttData {
  tasks: GanttTask[];
  timeRange: {
    start: string;
    end: string;
  };
  milestones: GanttTask[];
}

// Common UI State Types
export interface ComponentState {
  loading: boolean;
  error: string | null;
  isEditing: boolean;
  selectedItem: string | null;
  filters: Record<string, any>;
  viewMode: 'view' | 'edit' | 'export';
}

// Export Data Types
export interface ExportData {
  type: 'png' | 'csv';
  filename: string;
  data: any;
  timestamp: string;
}

// Component Props Extensions
export interface InteractiveComponentProps extends BaseInteractiveProps {
  className?: string;
  height?: number | string;
  showControls?: boolean;
  showExportButtons?: boolean;
  readOnly?: boolean;
  theme?: 'light' | 'dark';
}

