src/components/interactive/PortersFiveFo.content:
import React, { useState, useEffect } from 'react';
import { Button, Card, CardBody, Input, Textarea, Chip, Slider, Select, SelectItem } from '@heroui/react';
import { Icon } from '@iconify/react';
import BaseInteractiveLayout from './BaseInteractiveLayout';
import { InteractiveComponentProps, PorterData, ForceData } from './types';

// Sample data for Porter's Five Forces
const initialPorterData: PorterData = {
  forces: [
    {
      key: 'rivalry',
      labelZh: 'Competitive Rivalry',
      labelEn: 'Competitive Rivalry',
      weight: 3,
      description: 'Intensity of competition among existing players',
      descriptionEn: 'Intensity of competition among existing players',
      factors: ['Number of competitors', 'Product differentiation', 'Switching costs', 'Exit barriers'],
      factorsEn: ['Number of competitors', 'Product differentiation', 'Switching costs', 'Exit barriers']
    },
    {
      key: 'suppliers',
      labelZh: 'Supplier Power',
      labelEn: 'Supplier Power',
      weight: 2,
      description: 'Suppliers\' control over pricing and terms',
      descriptionEn: 'Suppliers\' control over pricing and terms',
      factors: ['Supplier concentration', 'Substitute inputs', 'Forward integration threat', 'Industry importance'],
      factorsEn: ['Supplier concentration', 'Substitute inputs', 'Forward integration threat', 'Industry importance']
    },
    {
      key: 'buyers',
      labelZh: 'Buyer Power',
      labelEn: 'Buyer Power',
      weight: 4,
      description: 'Customers\' bargaining power over prices and terms',
      descriptionEn: 'Customers\' bargaining power over prices and terms',
      factors: ['Buyer concentration', 'Purchase volume', 'Product standardization', 'Backward integration threat'],
      factorsEn: ['Buyer concentration', 'Purchase volume', 'Product standardization', 'Backward integration threat']
    },
    {
      key: 'substitutes',
      labelZh: 'Threat of Substitutes',
      labelEn: 'Threat of Substitutes',
      weight: 3,
      description: 'Threat level from alternative products or services',
      descriptionEn: 'Threat level from alternative products or services',
      factors: ['Substitute performance', 'Price advantage', 'Switching costs', 'Customer preference'],
      factorsEn: ['Substitute performance', 'Price advantage', 'Switching costs', 'Customer preference']
    },
    {
      key: 'entrants',
      labelZh: 'Threat of New Entrants',
      labelEn: 'Threat of New Entrants',
      weight: 2,
      description: 'Likelihood of new competitors entering the market',
      descriptionEn: 'Likelihood of new competitors entering the market',
      factors: ['Entry barriers', 'Economies of scale', 'Capital requirements', 'Government policy'],
      factorsEn: ['Entry barriers', 'Economies of scale', 'Capital requirements', 'Government policy']
    }
  ],
  overallRating: 2.8,
  summary: 'The industry is highly competitive with strong buyer power, but entry barriers are relatively high.',
  summaryEn: 'The industry is highly competitive with strong buyer power, but entry barriers are relatively high.'
};

interface PortersFiveForcesDiagramProps extends InteractiveComponentProps {}

const PortersFiveForcesDiagram: React.FC<PortersFiveForcesDiagramProps> = ({
  lang = 'zh',
  data: initialData,
  onDataChange,
  onExport,
  readOnly = false,
  showExportButtons = true,
  className = ''
}) => {
  const [data, setData] = useState<PorterData>(initialData || initialPorterData);
  const [isEditing, setIsEditing] = useState(false);
  const [selectedForce, setSelectedForce] = useState<string | null>(null);

  useEffect(() => {
    if (initialData) {
      setData(initialData);
    }
  }, [initialData]);

  const updateData = (newData: PorterData) => {
    setData(newData);
    onDataChange?.(newData);
  };

  const updateForce = (forceKey: string, updates: Partial<ForceData>) => {
    const newData = {
      ...data,
      forces: data.forces.map(force =>
        force.key === forceKey ? { ...force, ...updates } : force
      )
    };

    // Recalculate overall rating
    const avgWeight = newData.forces.reduce((sum, f) => sum + f.weight, 0) / newData.forces.length;
    newData.overallRating = Math.round(avgWeight * 10) / 10;

    updateData(newData);
  };

  const handleExportData = (type: 'png' | 'csv') => {
    if (type === 'csv') {
      const csvData = data.forces.map(force => ({
        Force: lang === 'en' ? force.labelEn : force.labelZh,
        Weight: force.weight,
        Description: lang === 'en' ? force.descriptionEn || force.description : force.description,
        Factors: (lang === 'en' ? force.factorsEn || force.factors : force.factors)?.join('; ')
      }));
      onExport?.(type, csvData);
    } else {
      onExport?.(type, data);
    }
  };

  const getForceColor = (weight: number) => {
    if (weight >= 4) return 'danger';
    if (weight >= 3) return 'warning';
    return 'success';
  };

  const getIntensityLabel = (weight: number) => {
    if (weight >= 4) return lang === 'zh' ? '高威胁' : 'High Threat';
    if (weight >= 3) return lang === 'zh' ? '中威胁' : 'Medium Threat';
    return lang === 'zh' ? '低威胁' : 'Low Threat';
  };

  const controls = !readOnly && (
    <Button
      size="sm"
      variant={isEditing ? 'solid' : 'flat'}
      color={isEditing ? 'primary' : 'default'}
      startContent={<Icon icon={isEditing ? 'lucide:check' : 'lucide:edit-3'} />}
      onPress={() => setIsEditing(!isEditing)}
    >
      {isEditing ? (lang === 'zh' ? '完成' : 'Done') : (lang === 'zh' ? '编辑' : 'Edit')}
    </Button>
  );

  return (
    <BaseInteractiveLayout
      title="波特五力分析"
      titleEn="Porter's Five Forces Analysis"
      lang={lang}
      onExport={handleExportData}
      showExportButtons={showExportButtons}
      controls={controls}
      className={className}
      data={data}
      pathLabels={{
        chapter: lang === 'zh' ? '第1章' : 'Chapter 1',
        subsection: lang === 'zh' ? '1.2 行业结构' : '1.2 Industry Structure'
      }}
    >
      <div className="space-y-6">
        {/* Overall Assessment */}
        <Card className="bg-gradient-to-r from-primary/5 to-secondary/5">
          <CardBody className="p-4">
            <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
              <div>
                <h4 className="text-lg font-semibold mb-2">
                  {lang === 'zh' ? '整体行业吸引力' : 'Overall Industry Attractiveness'}
                </h4>
                <p className="text-sm text-default-600">
                  {lang === 'en' ? data.summaryEn || data.summary : data.summary}
                </p>
              </div>
              <div className="flex items-center gap-3">
                <div className="text-center">
                  <div className="text-2xl font-bold text-primary">{data.overallRating}</div>
                  <div className="text-xs text-default-500">{lang === 'zh' ? '平均分' : 'Average'}</div>
                </div>
                <Chip
                  color={getForceColor(data.overallRating || 0)}
                  variant="flat"
                  size="sm"
                >
                  {getIntensityLabel(data.overallRating || 0)}
                </Chip>
              </div>
            </div>
          </CardBody>
        </Card>

        {/* Forces Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {data.forces.map((force) => (
            <Card 
              key={force.key} 
              isPressable={isEditing}
              className={`transition-all ${selectedForce === force.key ? 'ring-2 ring-primary' : ''}`}
              onPress={() => isEditing && setSelectedForce(selectedForce === force.key ? null : force.key)}
            >
              <CardBody className="p-4">
                <div className="space-y-4">
                  {/* Force Header */}
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <h5 className="font-semibold text-sm mb-1">
                        {lang === 'en' ? force.labelEn : force.labelZh}
                      </h5>
                      <p className="text-xs text-default-500 line-clamp-2">
                        {lang === 'en' ? force.descriptionEn || force.description : force.description}
                      </p>
                    </div>
                    <Chip
                      color={getForceColor(force.weight)}
                      variant="flat"
                      size="sm"
                      className="ml-2"
                    >
                      {force.weight}
                    </Chip>
                  </div>

                  {/* Weight Slider */}
                  {isEditing && selectedForce === force.key ? (
                    <div className="space-y-3">
                      <Slider
                        label={lang === 'zh' ? '威胁程度' : 'Threat Level'}
                        step={1}
                        maxValue={5}
                        minValue={1}
                        value={force.weight}
                        onChange={(value) => updateForce(force.key, { weight: value as number })}
                        className="max-w-md"
                        color={getForceColor(force.weight)}
                        marks={[
                          { value: 1, label: '1' },
                          { value: 2, label: '2' },
                          { value: 3, label: '3' },
                          { value: 4, label: '4' },
                          { value: 5, label: '5' }
                        ]}
                      />
                      <Textarea
                        label={lang === 'zh' ? '关键因素' : 'Key Factors'}
                        placeholder={lang === 'zh' ? '输入影响因素...' : 'Enter influencing factors...'}
                        value={(lang === 'en' ? force.factorsEn || force.factors : force.factors)?.join('\n')}
                        onChange={(e) => {
                          const factors = e.target.value.split('\n').filter(f => f.trim());
                          if (lang === 'en') {
                            updateForce(force.key, { factorsEn: factors });
                          } else {
                            updateForce(force.key, { factors });
                          }
                        }}
                        minRows={3}
                        size="sm"
                      />
                    </div>
                  ) : (
                    <div>
                      {/* Factors List */}
                      <div className="space-y-1">
                        {(lang === 'en' ? force.factorsEn || force.factors : force.factors)?.slice(0, 3).map((factor, idx) => (
                          <div key={idx} className="flex items-center gap-2 text-xs">
                            <Icon icon="lucide:dot" className="text-default-400" />
                            <span className="text-default-600">{factor}</span>
                          </div>
                        ))}
                        {((lang === 'en' ? force.factorsEn || force.factors : force.factors)?.length || 0) > 3 && (
                          <div className="text-xs text-default-400">
                            +{((lang === 'en' ? force.factorsEn || force.factors : force.factors)?.length || 0) - 3} {lang === 'zh' ? '更多' : 'more'}
                          </div>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              </CardBody>
            </Card>
          ))}
        </div>

        {/* Radar Chart Visualization */}
        <Card>
          <CardBody className="p-6">
            <h4 className="text-lg font-semibold mb-4">
              {lang === 'zh' ? '五力雷达图' : 'Five Forces Radar'}
            </h4>
            <div className="flex items-center justify-center h-64 bg-default-50 rounded-lg">
              <div className="text-center space-y-2">
                <Icon icon="lucide:radar" className="text-4xl text-default-300 mx-auto" />
                <p className="text-default-500">
                  {lang === 'zh' ? '雷达图可视化' : 'Radar Chart Visualization'}
                </p>
                <p className="text-xs text-default-400">
                  {lang === 'zh' ? '集成图表库后显示' : 'Shows when chart library integrated'}
                </p>
              </div>
            </div>
          </CardBody>
        </Card>

        {/* Analysis Summary */}
        {isEditing && (
          <Card>
            <CardBody className="p-4">
              <Textarea
                label={lang === 'zh' ? '分析总结' : 'Analysis Summary'}
                placeholder={lang === 'zh' ? '输入行业分析总结...' : 'Enter industry analysis summary...'}
                value={lang === 'en' ? data.summaryEn || data.summary : data.summary}
                onChange={(e) => {
                  if (lang === 'en') {
                    updateData({ ...data, summaryEn: e.target.value });
                  } else {
                    updateData({ ...data, summary: e.target.value });
                  }
                }}
                minRows={2}
              />
            </CardBody>
          </Card>
        )}
      </div>
    </BaseInteractiveLayout>
  );
};

export default PortersFiveForcesDiagram;

plugins/babel-plugin-inject-data-locator.content:
import type {NodePath} from "@babel/traverse";
import type * as BabelTypes from "@babel/types";
import type {RawSourceMap} from "source-map-js"; // Added RawSourceMap type

import {SourceMapConsumer} from "source-map-js";
import {threeJsComponents} from "./threejs-components";

// Define types for Babel file and state objects
interface BabelFile {
  opts: {
    filename?: string;
    [key: string]: unknown;
  };
}

interface BabelState {
  file: BabelFile;
  [key: string]: unknown;
}

// Define a type for error objects
type ErrorWithMessage = Error & {
  message: string;
};

interface CustomPluginOptions {
  inputSourceMap?: RawSourceMap | string; // Specify type for inputSourceMap
  types: typeof BabelTypes;
}

// Context tracking for Three.js components
interface ContextInfo {
  isThreeJsContext: boolean;
  depth: number;
}

// Helper to create the plugin object, allows cleaner passing of `t` and options
const createPluginLogic = (babel: {types: typeof BabelTypes}, options: CustomPluginOptions) => {
  const t = babel.types;
  const consumerHolder: {consumer?: SourceMapConsumer} = {};

  // Context stack to track Three.js contexts
  const contextStack: ContextInfo[] = [];

  // Helper functions for context management
  const isThreeJsComponent = (elementName: string): boolean => {
    return threeJsComponents.has(elementName);
  };

  const isCurrentlyInThreeJsContext = (): boolean => {
    return (
      contextStack.length > 0 && contextStack[contextStack.length - 1]?.isThreeJsContext === true
    );
  };

  const pushContext = (isThreeJs: boolean): void => {
    contextStack.push({
      depth: contextStack.length,
      isThreeJsContext: isThreeJs,
    });
  };

  const popContext = (): void => {
    contextStack.pop();
  };

  return {
    name: "inject-data-locator-original-source",
    post() {
      // Removed `file` as it's not used here
      // SourceMapConsumer from `source-map-js` does not have a destroy method.
      // Clearing the reference is enough for GC if needed.
      consumerHolder.consumer = undefined;
    },
    pre(file: BabelFile) {
      // `file` is Babel's File object, has `opts` like `filename`
      if (options.inputSourceMap) {
        try {
          let rawMap: RawSourceMap;

          if (typeof options.inputSourceMap === "string") {
            rawMap = JSON.parse(options.inputSourceMap) as RawSourceMap;
          } else {
            rawMap = options.inputSourceMap;
          }
          consumerHolder.consumer = new SourceMapConsumer(rawMap);
        } catch (errCaught: unknown) {
          const error = errCaught as ErrorWithMessage;

          console.warn(
            `[inject-data-locator-original-source] Failed to initialize SourceMapConsumer for ${file.opts.filename}:`,
            error.message,
          );
          consumerHolder.consumer = undefined;
        }
      } else {
        consumerHolder.consumer = undefined;
      }
    },
    visitor: {
      JSXElement: {
        enter(path: NodePath<BabelTypes.JSXElement>, state: BabelState) {
          const openingElement = path.node.openingElement;

          // Determine the element name
          let elementName = "Unknown";

          if (t.isJSXIdentifier(openingElement.name)) {
            elementName = openingElement.name.name;
          } else if (t.isJSXMemberExpression(openingElement.name)) {
            elementName = openingElement.name.property.name;
          }

          // Skip React Fragments
          if (elementName === "Fragment") {
            return;
          }

          // Check if this is a Three.js component
          const isThreeJsComp = isThreeJsComponent(elementName);

          // Push context when entering any element
          // If current element is Three.js OR we're already in Three.js context, mark as Three.js context
          pushContext(isThreeJsComp || isCurrentlyInThreeJsContext());

          // If we're in a Three.js context, skip data-locator injection entirely
          if (isCurrentlyInThreeJsContext()) {
            console.log(
              `[inject-data-locator-original-source] Skipping data-locator injection for ${elementName} (inside Three.js context, ${threeJsComponents.size} components loaded)`,
            );
            return;
          }

          // Continue with normal data-locator injection for non-Three.js elements
          const currentConsumer = consumerHolder.consumer;
          const attributes = openingElement.attributes;
          const filename = state.file.opts.filename || "unknown";

          let filePath = filename;
          const srcIndex = filename.lastIndexOf("/src/");

          if (srcIndex !== -1) {
            filePath = filename.substring(srcIndex + 1);
          } else {
            filePath = filename.split("/").pop() || filename;
          }

          const hasDataLocator = attributes.some(
            (attr: BabelTypes.JSXAttribute | BabelTypes.JSXSpreadAttribute) =>
              t.isJSXAttribute(attr) && attr.name.name === "data-locator",
          );

          if (!hasDataLocator && path.node.loc) {
            const {start} = path.node.loc;
            let finalLine = start.line; // 1-indexed
            let finalColumn = start.column; // 0-indexed
            let mapped = false;

            if (currentConsumer) {
              try {
                const originalPosition = currentConsumer.originalPositionFor({
                  // 1-indexed for lookup
                  column: start.column,
                  line: start.line, // 0-indexed for lookup
                });

                if (
                  originalPosition &&
                  originalPosition.line != null &&
                  originalPosition.column != null
                ) {
                  finalLine = originalPosition.line; // 1-indexed from sourcemap
                  finalColumn = originalPosition.column; // 0-indexed from sourcemap
                  mapped = true;
                }
              } catch (errCaught: unknown) {
                const error = errCaught as ErrorWithMessage;

                console.warn(
                  `[inject-data-locator-original-source] Error during source map lookup for ${elementName} in ${filename}:L${start.line}:C${start.column}`,
                  error.message,
                );
              }
            }

            const locatorValue = `${filePath}:${elementName}:${finalLine}:${finalColumn}`;
            const dataLocatorAttr = t.jsxAttribute(
              t.jsxIdentifier("data-locator"),
              t.stringLiteral(locatorValue),
            );

            openingElement.attributes.push(dataLocatorAttr);

            console.log(
              `[inject-data-locator-original-source] Added data-locator (${mapped ? "original" : "generated"}): ${locatorValue} to ${elementName} in ${filename}${mapped ? ` (gen L${start.line}:C${start.column})` : `(L${start.line}:C${start.column})`}`,
            );
          }
        },
        exit() {
          // Pop context when exiting any JSX element
          popContext();
        },
      },
    },
  };
};

// Babel plugin signature: a function that returns the plugin object.
// Receives babel object (with types, etc.) as first argument, and plugin options as second.
export default function (
  babelAPI: {types: typeof BabelTypes; assertVersion: (version: number) => void},
  options: CustomPluginOptions,
) {
  babelAPI.assertVersion(7); // Ensure compatibility with Babel 7+

  return createPluginLogic(babelAPI, options);
}

src/components/interactive/BusinessOppor.content:
import React, { useState, useEffect } from 'react';
import { Button, Card, CardBody, Textarea, Chip, Input } from '@heroui/react';
import { Icon } from '@iconify/react';
import BaseInteractiveLayout from './BaseInteractiveLayout';
import { InteractiveComponentProps, OpportunityData } from './types';

// Sample data for Business Opportunity Statement
const initialOpportunityData: OpportunityData = {
  who: '25-40 year old working professionals who need to maintain learning and personal development while managing busy careers',
  whoEn: '25-40 year old working professionals who need to maintain learning and personal development while managing busy careers',
  what: 'Lack of time and personalized learning paths, leading to stagnant skill development and career progression obstacles',
  whatEn: 'Lack of time and personalized learning paths, leading to stagnant skill development and career progression obstacles',
  why: 'AI-driven personalized learning platform providing micro-learning and intelligent recommendations to help users efficiently develop career skills',
  whyEn: 'AI-driven personalized learning platform providing micro-learning and intelligent recommendations to help users efficiently develop career skills',
  how: 'Over 500K monthly active users, 200% increase in average learning time, 70% of users receive promotions or raises within 6 months',
  howEn: 'Over 500K monthly active users, 200% increase in average learning time, 70% of users receive promotions or raises within 6 months',
  assumptions: [
    'Target users are willing to pay for personalized learning experiences',
    'AI recommendation algorithms can effectively improve learning efficiency',
    'Enterprise customers will pay for employee learning and development',
    'Mobile learning becomes the mainstream learning method'
  ],
  assumptionsEn: [
    'Target users are willing to pay for personalized learning experiences',
    'AI recommendation algorithms can effectively improve learning efficiency',
    'Enterprise customers will pay for employee learning and development',
    'Mobile learning becomes the mainstream learning method'
  ],
  validationPlan: {
    'User Interviews': 'Conduct in-depth interviews with 50 target users to understand learning pain points and willingness to pay',
    'Prototype Testing': 'Develop MVP product and invite 100 users for 1-month testing',
    'Market Research': 'Analyze competitor pricing strategies and user reviews',
    'Enterprise Pilot': 'Partner with 5 companies to test B2B market demand'
  },
  validationPlanEn: {
    'User Interviews': 'Conduct in-depth interviews with 50 target users to understand learning pain points and willingness to pay',
    'Prototype Testing': 'Develop MVP product and invite 100 users for 1-month testing',
    'Market Research': 'Analyze competitor pricing strategies and user reviews',
    'Enterprise Pilot': 'Partner with 5 companies to test B2B market demand'
  }
};

interface BusinessOpportunityDiagramProps extends InteractiveComponentProps {}

const BusinessOpportunityDiagram: React.FC<BusinessOpportunityDiagramProps> = ({
  lang = 'zh',
  data: initialData,
  onDataChange,
  onExport,
  readOnly = false,
  showExportButtons = true,
  className = ''
}) => {
  const [data, setData] = useState<OpportunityData>(initialData || initialOpportunityData);
  const [isEditing, setIsEditing] = useState(false);
  const [activeSection, setActiveSection] = useState<string | null>(null);

  useEffect(() => {
    if (initialData) {
      setData(initialData);
    }
  }, [initialData]);

  const updateData = (newData: OpportunityData) => {
    setData(newData);
    onDataChange?.(newData);
  };

  const addAssumption = () => {
    const newAssumption = lang === 'zh' ? '新假设' : 'New Assumption';
    if (lang === 'en') {
      updateData({
        ...data,
        assumptionsEn: [...(data.assumptionsEn || []), newAssumption]
      });
    } else {
      updateData({
        ...data,
        assumptions: [...data.assumptions, newAssumption]
      });
    }
  };

  const updateAssumption = (index: number, value: string) => {
    if (lang === 'en') {
      const newAssumptions = [...(data.assumptionsEn || [])];
      newAssumptions[index] = value;
      updateData({ ...data, assumptionsEn: newAssumptions });
    } else {
      const newAssumptions = [...data.assumptions];
      newAssumptions[index] = value;
      updateData({ ...data, assumptions: newAssumptions });
    }
  };

  const removeAssumption = (index: number) => {
    if (lang === 'en') {
      const newAssumptions = [...(data.assumptionsEn || [])];
      newAssumptions.splice(index, 1);
      updateData({ ...data, assumptionsEn: newAssumptions });
    } else {
      const newAssumptions = [...data.assumptions];
      newAssumptions.splice(index, 1);
      updateData({ ...data, assumptions: newAssumptions });
    }
  };

  const updateValidationPlan = (key: string, value: string) => {
    if (lang === 'en') {
      updateData({
        ...data,
        validationPlanEn: { ...data.validationPlanEn, [key]: value }
      });
    } else {
      updateData({
        ...data,
        validationPlan: { ...data.validationPlan, [key]: value }
      });
    }
  };

  const addValidationItem = () => {
    const newKey = lang === 'zh' ? '新验证项' : 'New Validation Item';
    const newValue = lang === 'zh' ? '描述验证方法...' : 'Describe validation method...';

    if (lang === 'en') {
      updateData({
        ...data,
        validationPlanEn: { ...data.validationPlanEn, [newKey]: newValue }
      });
    } else {
      updateData({
        ...data,
        validationPlan: { ...data.validationPlan, [newKey]: newValue }
      });
    }
  };

  const removeValidationItem = (key: string) => {
    if (lang === 'en') {
      const newPlan = { ...data.validationPlanEn };
      delete newPlan[key];
      updateData({ ...data, validationPlanEn: newPlan });
    } else {
      const newPlan = { ...data.validationPlan };
      delete newPlan[key];
      updateData({ ...data, validationPlan: newPlan });
    }
  };

  const handleExportData = (type: 'png' | 'csv') => {
    if (type === 'csv') {
      const csvData = [
        {
          Section: 'Who',
          Content: lang === 'en' ? data.whoEn || data.who : data.who
        },
        {
          Section: 'What',
          Content: lang === 'en' ? data.whatEn || data.what : data.what
        },
        {
          Section: 'Why',
          Content: lang === 'en' ? data.whyEn || data.why : data.why
        },
        {
          Section: 'How',
          Content: lang === 'en' ? data.howEn || data.how : data.how
        },
        ...((lang === 'en' ? data.assumptionsEn || data.assumptions : data.assumptions) || []).map((assumption, index) => ({
          Section: `Assumption ${index + 1}`,
          Content: assumption
        })),
        ...Object.entries(lang === 'en' ? data.validationPlanEn || data.validationPlan : data.validationPlan).map(([key, value]) => ({
          Section: `Validation: ${key}`,
          Content: value
        }))
      ];
      onExport?.(type, csvData);
    } else {
      onExport?.(type, data);
    }
  };

  const opportunitySections = [
    {
      key: 'who',
      title: lang === 'zh' ? '谁 (WHO)' : 'WHO',
      subtitle: lang === 'zh' ? '目标客户是谁？' : 'Who is the target customer?',
      value: lang === 'en' ? data.whoEn || data.who : data.who,
      placeholder: lang === 'zh' ? '描述你的目标客户群体...' : 'Describe your target customer segment...',
      icon: 'lucide:users',
      color: 'primary'
    },
    {
      key: 'what',
      title: lang === 'zh' ? '什么 (WHAT)' : 'WHAT',
      subtitle: lang === 'zh' ? '核心痛点是什么？' : 'What is the core pain point?',
      value: lang === 'en' ? data.whatEn || data.what : data.what,
      placeholder: lang === 'zh' ? '描述客户面临的主要问题...' : 'Describe the main problem customers face...',
      icon: 'lucide:target',
      color: 'danger'
    },
    {
      key: 'why',
      title: lang === 'zh' ? '为什么 (WHY)' : 'WHY',
      subtitle: lang === 'zh' ? '你的解决方案价值主张？' : 'What is your solution\'s value proposition?',
      value: lang === 'en' ? data.whyEn || data.why : data.why,
      placeholder: lang === 'zh' ? '描述你的解决方案和独特价值...' : 'Describe your solution and unique value...',
      icon: 'lucide:lightbulb',
      color: 'warning'
    },
    {
      key: 'how',
      title: lang === 'zh' ? '如何 (HOW)' : 'HOW',
      subtitle: lang === 'zh' ? '成功的衡量标准？' : 'What are the success metrics?',
      value: lang === 'en' ? data.howEn || data.how : data.how,
      placeholder: lang === 'zh' ? '定义成功的关键指标和目标...' : 'Define key success metrics and targets...',
      icon: 'lucide:trending-up',
      color: 'success'
    }
  ];

  const controls = !readOnly && (
    <Button
      size="sm"
      variant={isEditing ? 'solid' : 'flat'}
      color={isEditing ? 'primary' : 'default'}
      startContent={<Icon icon={isEditing ? 'lucide:check' : 'lucide:edit-3'} />}
      onPress={() => {
        setIsEditing(!isEditing);
        setActiveSection(null);
      }}
    >
      {isEditing ? (lang === 'zh' ? '完成' : 'Done') : (lang === 'zh' ? '编辑' : 'Edit')}
    </Button>
  );

  return (
    <BaseInteractiveLayout
      title="商业机会陈述"
      titleEn="Business Opportunity Statement"
      lang={lang}
      onExport={handleExportData}
      showExportButtons={showExportButtons}
      controls={controls}
      className={className}
      data={data}
      pathLabels={{
        chapter: lang === 'zh' ? '第1章' : 'Chapter 1',
        subsection: lang === 'zh' ? '1.3 机会空间' : '1.3 Opportunity Space'
      }}
    >
      <div className="space-y-6" id="business-opportunity-diagram">
        {/* Main Opportunity Canvas */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {opportunitySections.map((section) => (
            <Card 
              key={section.key}
              isPressable={isEditing}
              className={`transition-all h-full ${
                activeSection === section.key ? `ring-2 ring-${section.color}` : ''
              }`}
              onPress={() => isEditing && setActiveSection(activeSection === section.key ? null : section.key)}
            >
              <CardBody className="p-4 h-full flex flex-col">
                <div className="flex items-center gap-3 mb-3">
                  <Icon
                    icon={section.icon}
                    className={`text-xl text-${section.color}`}
                  />
                  <div>
                    <h4 className="text-lg font-semibold">{section.title}</h4>
                    <p className="text-xs text-default-500">{section.subtitle}</p>
                  </div>
                  {isEditing && activeSection === section.key && (
                    <Chip color={section.color} variant="flat" size="sm">
                      {lang === 'zh' ? '编辑中' : 'Editing'}
                    </Chip>
                  )}
                </div>

                <div className="flex-1">
                  {isEditing && activeSection === section.key ? (
                    <Textarea
                      value={section.value}
                      onChange={(e) => {
                        const key = section.key as keyof OpportunityData;
                        if (lang === 'en') {
                          const enKey = `${key}En` as keyof OpportunityData;
                          updateData({ ...data, [enKey]: e.target.value });
                        } else {
                          updateData({ ...data, [key]: e.target.value });
                        }
                      }}
                      placeholder={section.placeholder}
                      minRows={4}
                      className="h-full"
                    />
                  ) : (
                    <div className="bg-default-50 rounded-lg p-3 h-full flex items-center">
                      <p className="text-sm text-default-700 leading-relaxed">
                        {section.value || (lang === 'zh' ? '点击编辑添加内容...' : 'Click edit to add content...')}
                      </p>
                    </div>
                  )}
                </div>
              </CardBody>
            </Card>
          ))}
        </div>

        {/* Key Assumptions */}
        <Card>
          <CardBody className="p-4">
            <div className="flex items-center justify-between mb-4">
              <h4 className="text-lg font-semibold flex items-center gap-2">
                <Icon icon="lucide:help-circle" className="text-warning" />
                {lang === 'zh' ? '关键假设' : 'Key Assumptions'}
              </h4>
              {isEditing && (
                <Button
                  size="sm"
                  variant="flat"
                  color="warning"
                  startContent={<Icon icon="lucide:plus" />}
                  onPress={addAssumption}
                >
                  {lang === 'zh' ? '添加假设' : 'Add Assumption'}
                </Button>
              )}
            </div>

            <div className="space-y-3">
              {(lang === 'en' ? data.assumptionsEn || data.assumptions : data.assumptions).map((assumption, index) => (
                <Card key={index} className="border">
                  <CardBody className="p-3">
                    <div className="flex items-start gap-3">
                      <Chip color="warning" variant="flat" size="sm">
                        {index + 1}
                      </Chip>
                      <div className="flex-1">
                        {isEditing ? (
                          <Input
                            value={assumption}
                            onChange={(e) => updateAssumption(index, e.target.value)}
                            placeholder={lang === 'zh' ? '输入关键假设...' : 'Enter key assumption...'}
                            size="sm"
                          />
                        ) : (
                          <p className="text-sm text-default-700">{assumption}</p>
                        )}
                      </div>
                      {isEditing && (
                        <Button
                          size="sm"
                          variant="flat"
                          color="danger"
                          isIconOnly
                          onPress={() => removeAssumption(index)}
                        >
                          <Icon icon="lucide:x" />
                        </Button>
                      )}
                    </div>
                  </CardBody>
                </Card>
              ))}
            </div>
          </CardBody>
        </Card>

        {/* Validation Plan */}
        <Card>
          <CardBody className="p-4">
            <div className="flex items-center justify-between mb-4">
              <h4 className="text-lg font-semibold flex items-center gap-2">
                <Icon icon="lucide:check-circle" className="text-success" />
                {lang === 'zh' ? '验证计划' : 'Validation Plan'}
              </h4>
              {isEditing && (
                <Button
                  size="sm"
                  variant="flat"
                  color="success"
                  startContent={<Icon icon="lucide:plus" />}
                  onPress={addValidationItem}
                >
                  {lang === 'zh' ? '添加验证项' : 'Add Validation Item'}
                </Button>
              )}
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {Object.entries(lang === 'en' ? data.validationPlanEn || data.validationPlan : data.validationPlan).map(([key, value]) => (
                <Card key={key} className="border">
                  <CardBody className="p-3">
                    <div className="space-y-3">
                      <div className="flex items-center justify-between">
                        <h5 className="font-semibold text-sm flex items-center gap-2">
                          <Icon icon="lucide:clipboard-check" className="text-success" />
                          {isEditing ? (
                            <Input
                              value={key}
                              onChange={(e) => {
                                // Remove old key and add new one
                                const oldPlan = lang === 'en' ? data.validationPlanEn || data.validationPlan : data.validationPlan;
                                const newPlan = { ...oldPlan };
                                delete newPlan[key];
                                newPlan[e.target.value] = value;

                                if (lang === 'en') {
                                  updateData({ ...data, validationPlanEn: newPlan });
                                } else {
                                  updateData({ ...data, validationPlan: newPlan });
                                }
                              }}
                              size="sm"
                              className="max-w-32"
                            />
                          ) : (
                            key
                          )}
                        </h5>
                        {isEditing && (
                          <Button
                            size="sm"
                            variant="flat"
                            color="danger"
                            isIconOnly
                            onPress={() => removeValidationItem(key)}
                          >
                            <Icon icon="lucide:trash" />
                          </Button>
                        )}
                      </div>

                      {isEditing ? (
                        <Textarea
                          value={value}
                          onChange={(e) => updateValidationPlan(key, e.target.value)}
                          placeholder={lang === 'zh' ? '描述验证方法...' : 'Describe validation method...'}
                          minRows={2}
                          size="sm"
                        />
                      ) : (
                        <p className="text-xs text-default-600">{value}</p>
                      )}
                    </div>
                  </CardBody>
                </Card>
              ))}
            </div>
          </CardBody>
        </Card>

        {/* Summary Export Preview */}
        <Card className="bg-gradient-to-r from-primary/5 to-secondary/5">
          <CardBody className="p-4">
            <div className="flex items-center gap-2 mb-3">
              <Icon icon="lucide:file-text" className="text-primary" />
              <h4 className="text-lg font-semibold">
                {lang === 'zh' ? '机会陈述摘要' : 'Opportunity Statement Summary'}
              </h4>
            </div>
            <div className="bg-background/50 rounded-lg p-4">
              <p className="text-sm text-default-700 leading-relaxed">
                <strong>{lang === 'zh' ? '目标客户：' : 'Target Customer: '}</strong>
                {lang === 'en' ? data.whoEn || data.who : data.who}
                <br /><br />
                <strong>{lang === 'zh' ? '核心痛点：' : 'Core Pain Point: '}</strong>
                {lang === 'en' ? data.whatEn || data.what : data.what}
                <br /><br />
                <strong>{lang === 'zh' ? '解决方案：' : 'Solution: '}</strong>
                {lang === 'en' ? data.whyEn || data.why : data.why}
                <br /><br />
                <strong>{lang === 'zh' ? '成功指标：' : 'Success Metrics: '}</strong>
                {lang === 'en' ? data.howEn || data.how : data.how}
              </p>
            </div>
          </CardBody>
        </Card>
      </div>
    </BaseInteractiveLayout>
  );
};

export default BusinessOpportunityDiagram;

src/components/SearchBar.tsx.content:
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { Input, Dropdown, DropdownTrigger, DropdownMenu, DropdownItem } from '@heroui/react';
import { Icon } from '@iconify/react';
import { searchFrameworks, Framework } from '../data/frameworks';
import { useI18n } from '../contexts/I18nContext';

const SearchBar: React.FC = () => {
  const { t, lang } = useI18n();
  const [query, setQuery] = useState('');
  const [results, setResults] = useState<Framework[]>([]);
  const [isOpen, setIsOpen] = useState(false);
  const history = useHistory();

  const handleSearch = (value: string) => {
    setQuery(value);

    if (value.trim().length > 1) {
      const searchResults = searchFrameworks(value);
      setResults(searchResults);
      setIsOpen(true);
    } else {
      setResults([]);
      setIsOpen(false);
    }
  };

  const handleSelectFramework = (slug: string) => {
    history.push(`/frameworks/${slug}`);
    setQuery('');
    setResults([]);
    setIsOpen(false);
  };

  return (
    <div className="w-full relative">
      <Dropdown isOpen={isOpen && results.length > 0} onOpenChange={setIsOpen}>
        <DropdownTrigger>
          <Input
            placeholder={t('search_placeholder')}
            value={query}
            onValueChange={handleSearch}
            startContent={<Icon icon="lucide:search" className="text-neutral-400" />}
            classNames={{
              input: "text-sm",
            }}
          />
        </DropdownTrigger>
        <DropdownMenu 
          aria-label={t('search_placeholder')} 
          className="w-full max-h-[300px] overflow-y-auto"
          onAction={(key) => handleSelectFramework(key as string)}
        >
          {results.map((framework) => (
            <DropdownItem key={framework.slug} description={lang === 'en' ? (framework.summaryEn || '') : (framework.summary || '')} textValue={lang === 'en' ? framework.englishTitle : framework.title}>
              <div className="flex flex-col gap-1">
                <p className="font-medium">{lang === 'en' ? framework.englishTitle : framework.title}</p>
                <div className="flex gap-1">
                  {(lang === 'en' ? (framework.tagsEn && framework.tagsEn.length ? framework.tagsEn : framework.tags) : framework.tags).slice(0, 2).map((tag, index) => (
                    <span key={index} className="text-xs bg-neutral-100 px-1 rounded-sm">
                      {tag}
                    </span>
                  ))}
                  {((lang === 'en' ? (framework.tagsEn && framework.tagsEn.length ? framework.tagsEn : framework.tags) : framework.tags).length > 2) && (
                    <span className="text-xs text-neutral-400">+{(lang === 'en' ? (framework.tagsEn && framework.tagsEn.length ? framework.tagsEn : framework.tags) : framework.tags).length - 2}</span>
                  )}
                </div>
              </div>
            </DropdownItem>
          ))}
          {results.length === 0 && query.trim().length > 1 && (
            <DropdownItem isDisabled className="text-center opacity-70">
              {t('search_no_result')}
            </DropdownItem>
          )}
        </DropdownMenu>
      </Dropdown>
    </div>
  );
};

export default SearchBar;

