import React, { useState } from 'react';
import { Icon } from '@iconify/react';
import { useI18n } from '../../contexts/I18nContext';

interface TreeNode {
  name: string;
  nameEn: string;
  children?: TreeNode[];
}

const Node: React.FC<{ node: TreeNode; level: number }> = ({ node, level }) => {
  const { lang } = useI18n();
  const [isExpanded, setIsExpanded] = useState(level < 2);

  return (
    <div style={{ marginLeft: level * 20 }}>
      <div className="flex items-center py-1">
        {node.children && (
          <Icon 
            icon={isExpanded ? 'lucide:chevron-down' : 'lucide:chevron-right'} 
            className="cursor-pointer mr-1" 
            onClick={() => setIsExpanded(!isExpanded)} 
          />
        )}
        <span className={`px-2 py-1 rounded ${level === 0 ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}>
          {lang === 'en' ? node.nameEn : node.name}
        </span>
      </div>
      {isExpanded && node.children && (
        <div>
          {node.children.map((child, index) => (
            <Node key={index} node={child} level={level + 1} />
          ))}
        </div>
      )}
    </div>
  );
};

const NorthStarFrameworkDiagram: React.FC = () => {
  const { t } = useI18n();

  const initialData: TreeNode = {
    name: 'Total Listening Time',
    nameEn: 'Total Listening Time',
    children: [
      { 
        name: 'Monthly Active Users',
        nameEn: 'Monthly Active Users',
        children: [
          { name: 'New User Registrations', nameEn: 'New User Registrations' },
          { name: 'User Retention Rate', nameEn: 'User Retention Rate' },
        ]
      },
      { 
        name: 'Listening Time per Session',
        nameEn: 'Listening Time per Session',
        children: [
          { name: 'Playlists Created', nameEn: 'Playlists Created' },
          { name: 'Songs Shared', nameEn: 'Songs Shared' },
        ]
      },
      { 
        name: 'Recommendation CTR',
        nameEn: 'Recommendation CTR',
        children: [
          { name: 'Algorithm Accuracy', nameEn: 'Algorithm Accuracy' },
          { name: 'UI Presentation Optimization', nameEn: 'UI Presentation Optimization' },
        ]
      },
    ],
  };

  return (
    <div className="bg-white p-6 rounded-lg">
      <h2 className="text-2xl font-bold mb-4 text-gray-800">{t('nsm_diagram_title', '北极星指标树（可折叠）')}</h2>
      <Node node={initialData} level={0} />
    </div>
  );
};

export default NorthStarFrameworkDiagram;

src/components/interactive/BaseInteracti.content:
import React from 'react';
import { Button, Card, CardBody, CardHeader, Spinner, Divider } from '@heroui/react';
import { Icon } from '@iconify/react';
import { InteractiveComponentProps } from './types';

interface BaseInteractiveLayoutProps extends InteractiveComponentProps {
  title: string;
  titleEn?: string;
  children: React.ReactNode;
  loading?: boolean;
  error?: string | null;
  controls?: React.ReactNode;
  pathLabels?: {
    chapter: string;
    subsection?: string;
  };
}

const BaseInteractiveLayout: React.FC<BaseInteractiveLayoutProps> = ({
  title,
  titleEn,
  children,
  loading = false,
  error = null,
  controls,
  pathLabels,
  lang,
  onExport,
  showExportButtons = true,
  className = '',
  ...props
}) => {
  const displayTitle = lang === 'en' && titleEn ? titleEn : title;

  const handleExport = (type: 'png' | 'csv') => {
    if (onExport) {
      onExport(type, props.data);
    }
  };

  if (loading) {
    return (
      <div className={`w-full aspect-video bg-content2 rounded-lg flex items-center justify-center ${className}`}>
        <Spinner size="lg" />
      </div>
    );
  }

  if (error) {
    return (
      <Card className={`w-full ${className}`}>
        <CardBody className="flex flex-col items-center justify-center py-16">
          <Icon icon="lucide:alert-circle" className="text-danger text-4xl mb-4" />
          <p className="text-danger font-medium mb-2">
            {lang === 'zh' ? '加载出错' : 'Error Loading'}
          </p>
          <p className="text-default-500 text-center">{error}</p>
        </CardBody>
      </Card>
    );
  }

  return (
    <div className={`w-full space-y-6 mt-8 ${className}`}>
      {/* Path Labels */}
      {pathLabels && (
        <div className="flex items-center gap-2 text-sm text-default-500">
          <span className="bg-primary/10 text-primary px-2 py-1 rounded-md">
            {pathLabels.chapter}
          </span>
          {pathLabels.subsection && (
            <>
              <Icon icon="lucide:chevron-right" className="text-default-300" />
              <span className="bg-default-100 px-2 py-1 rounded-md">
                {pathLabels.subsection}
              </span>
            </>
          )}
        </div>
      )}

      <Card className="w-full">
        <CardHeader className="flex flex-row items-center justify-between">
          <div className="flex items-center gap-3">
            <div className="bg-primary/10 p-2 rounded-lg">
              <Icon icon="lucide:activity" className="text-primary text-xl" />
            </div>
            <div>
              <h3 className="text-lg font-semibold">{displayTitle}</h3>
              <p className="text-sm text-default-500">
                {lang === 'zh' ? '交互式工具' : 'Interactive Tool'}
              </p>
            </div>
          </div>

          <div className="flex items-center gap-2">
            {controls}
            {showExportButtons && (
              <div className="flex gap-2">
                <Button
                  size="sm"
                  variant="flat"
                  startContent={<Icon icon="lucide:image" />}
                  onPress={() => handleExport('png')}
                >
                  {lang === 'zh' ? 'PNG 导出' : 'PNG'}
                </Button>
                <Button
                  size="sm"
                  variant="flat"
                  startContent={<Icon icon="lucide:table" />}
                  onPress={() => handleExport('csv')}
                >
                  {lang === 'zh' ? 'CSV 导出' : 'CSV'}
                </Button>
              </div>
            )}
          </div>
        </CardHeader>

        <Divider />

        <CardBody className="p-6">
          {children}
        </CardBody>
      </Card>

      {/* Mobile Note */}
      <div className="block md:hidden">
        <div className="bg-warning/10 border border-warning/20 rounded-lg p-4">
          <div className="flex items-start gap-3">
            <Icon icon="lucide:smartphone" className="text-warning text-xl flex-shrink-0 mt-0.5" />
            <div>
              <p className="text-sm font-medium text-warning-600">
                {lang === 'zh' ? '移动端提示' : 'Mobile Notice'}
              </p>
              <p className="text-xs text-default-600 mt-1">
                {lang === 'zh' 
                  ? '在移动设备上，此组件为只读预览模式。完整功能请在桌面端使用。'
                  : 'This component is in read-only preview mode on mobile. Use desktop for full functionality.'
                }
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BaseInteractiveLayout;

src/pages/Home.tsx.content:
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Button, Input, Chip } from '@heroui/react';
import { Icon } from '@iconify/react';
import { motion } from 'framer-motion';
import { getAllFrameworks, getAllChapters, Framework } from '../data/frameworks';
import FrameworkCard from '../components/ui/FrameworkCard';
import DoubleDiamondDiagram from '../components/ui/DoubleDiamondDiagram';
import { useI18n } from '../contexts/I18nContext';

const Home: React.FC = () => {
  const { t, lang } = useI18n();
  const frameworks = getAllFrameworks();
  const chapters = getAllChapters();
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedTags, setSelectedTags] = useState<string[]>([]);

  // Extract all unique tags
  const allTags = Array.from(
    new Set(frameworks.flatMap(framework => (lang === 'en' ? (framework.tagsEn && framework.tagsEn.length ? framework.tagsEn : framework.tags) : framework.tags)))
  ).sort();

  // Filter frameworks based on search query and selected tags
  const filteredFrameworks = frameworks.filter(framework => {
    const matchesSearch = searchQuery === '' || 
      framework.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      framework.englishTitle.toLowerCase().includes(searchQuery.toLowerCase()) ||
      framework.summary.toLowerCase().includes(searchQuery.toLowerCase());

    const tagSource = lang === 'en' ? (framework.tagsEn && framework.tagsEn.length ? framework.tagsEn : framework.tags) : framework.tags;
    const matchesTags = selectedTags.length === 0 || selectedTags.some(tag => tagSource.includes(tag));

    return matchesSearch && matchesTags;
  });

  // Group frameworks by chapter
  const frameworksByChapter = chapters.reduce((acc, chapter) => {
    acc[chapter.id] = filteredFrameworks.filter(f => f.chapter === chapter.id);
    return acc;
  }, {} as Record<string, Framework[]>);

  const toggleTag = (tag: string) => {
    if (selectedTags.includes(tag)) {
      setSelectedTags(selectedTags.filter(t => t !== tag));
    } else {
      setSelectedTags([...selectedTags, tag]);
    }
  };

  return (
    <div className="max-w-7xl mx-auto">
      {/* Hero Section */}
      <section className="mb-8">
        <div className="bg-primary-50 rounded-lg p-8">
          {/* Main Title */}
          <motion.div
            className="mb-8 text-center"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <h1 
              className="text-2xl font-semibold text-neutral-700 mb-2"
              style={{ fontFamily: 'Manrope, sans-serif' }}
            >
              {t('home_main_title')}
            </h1>
            <h2 
              className="text-5xl font-bold text-neutral-900"
              style={{ fontFamily: 'Manrope, sans-serif' }}
            >
              {t('home_subtitle')}
            </h2>
          </motion.div>

          {/* Double Diamond Diagram - Full Width */}
          <motion.div
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.4, delay: 0.1 }}
          >
            <DoubleDiamondDiagram />
          </motion.div>
        </div>
      </section>

      {/* Search and Filter */}
      <section className="mb-8">
        <div className="flex flex-col md:flex-row gap-4 items-start md:items-center">
          <Input
            placeholder={t('search_placeholder')}
            value={searchQuery}
            onValueChange={setSearchQuery}
            startContent={<Icon icon="lucide:search" className="text-neutral-400" />}
            className="w-full md:w-80"
          />
          <div className="flex-1 overflow-x-auto pb-2">
            <div className="flex gap-2 flex-nowrap min-w-max">
              {allTags.map(tag => (
                <Chip 
                  key={tag}
                  color={selectedTags.includes(tag) ? "primary" : "default"}
                  variant={selectedTags.includes(tag) ? "solid" : "flat"}
                  onClick={() => toggleTag(tag)}
                  className="cursor-pointer"
                >
                  {tag}
                </Chip>
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Frameworks by Chapter */}
      <div id="frameworks">
      {chapters.map(chapter => {
        const chapterFrameworks = frameworksByChapter[chapter.id] || [];
        if (chapterFrameworks.length === 0) return null;

        return (
          <section key={chapter.id} className="mb-8">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-2xl font-bold text-neutral-900">
                {t('chapter_prefix', { n: chapter.id })}: {lang === 'en' ? (chapter.titleEn || chapter.title) : chapter.title}
              </h2>
              <Button 
                as={Link}
                to={`/chapters/${chapter.id}`}
                variant="light"
                color="primary"
                size="sm"
                endContent={<Icon icon="lucide:chevron-right" />}
              >
                {t('chapter_view_all')}
              </Button>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 auto-rows-fr">
              {chapterFrameworks.slice(0, 3).map(framework => (
                <FrameworkCard key={framework.slug} framework={framework} />
              ))}
            </div>
          </section>
        );
      })}

      {/* No Results */}
      {Object.values(frameworksByChapter).every(arr => arr.length === 0) && (
        <div className="text-center py-8">
          <Icon icon="lucide:search-x" className="text-neutral-400 text-5xl mx-auto mb-4" />
          <h3 className="text-xl font-medium mb-2">{t('search_no_result')}</h3>
          <Button 
            color="primary" 
            variant="flat"
            onPress={() => {
              setSearchQuery('');
              setSelectedTags([]);
            }}
          >
            {lang === 'en' ? 'Clear Filters' : '清除筛选'}
          </Button>
        </div>
      )}
      </div>
    </div>
  );
};

export default Home;

